// page init
jQuery(function(){
	// jcf.customForms.replaceAll();
	//initCarousel();
	initSlideShow();
	initSlideShowNoBorder();
	initNewsSlideShow();
	initOpenClose();
	initInputs();
	//initSameHeight();
});

// scroll gallery init
function initCarousel() {
	jQuery('div.slide-col').scrollGallery({
		mask: 'div.mask',
		slider: '.carousel',
		slides: '.slide',
		pagerLinks: '.switcher>li',
		stretchSlideToMask: true,
		maskAutoSize: true,
		vertical: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('div.slide-block').fadeGallery({
		slides: '.slideshow>li',
		pagerLinks: '.switcher>li',
		event: 'click',
		autoRotation: true,
		autoHeight: true,
		switchTime: 5000,
		animSpeed: 500,
		pauseOnHover: false
	});
}

function initSlideShowNoBorder() {
	jQuery('div.slide-block-noborder').fadeGallery({
		slides: '.slideshow>li',
		pagerLinks: '.switcher>li',
		event: 'click',
		autoRotation: true,
		autoHeight: true,
		switchTime: 8000,
		animSpeed: 500,
		pauseOnHover: false
	});
}

function initNewsSlideShow() {
	jQuery('div.slideshow-holder').fadeGallery({
		slides: '.news-slideshow>li',
		pagerLinks: '.switcher>li',
		event: 'click',
		autoRotation: true,
		autoHeight: true,
		switchTime: 5000,
		animSpeed: 500
	});
}


// open-close init
function initOpenClose() {
	jQuery('div.expandable-box').openClose({
		activeClass: 'active',
		opener: '.opener',
		slider: '.slide',
		animSpeed: 400,
		effect: 'slide'
	});
}

// clear inputs on focus
function initInputs() {
	PlaceholderInput.replaceByOptions({
		// filter options
		clearInputs: true,
		clearTextareas: true,
		clearPasswords: true,
		skipClass: 'default',

		// input options
		wrapWithElement: false,
		showUntilTyping: false,
		getParentByClass: false,
		placeholderAttr: 'value'
	});
}

// align blocks height
function initSameHeight() {
	jQuery('#main').sameHeight({
		elements: '#content, #sidebar',
		flexible: true,
		multiLine: true
	});
}

